<?phpclass Admin_ConvitesController extends Zend_Controller_Action {    public function indexAction() {        $form = new TCS_Form_FormConvite();        $ModelConvites = new Admin_Model_UsuarioConvites();        $result = $ModelConvites->Listar();        $auth = Zend_Auth::getInstance();        $user = $auth->getIdentity();        $data = $this->_request->getPost();        if ($this->_request->isPost() && $form->isValid($data)) {            if (!empty($data['email'])) {                $conteudo = array('vendedor' => $user->nome, 'mensagem' => $data['mensagem']);                $assunto = "Comercialização Eletrônica Schoeler | Convite para Utilização";                Zend_Controller_Action_HelperBroker::getStaticHelper('Emails')->Emails('', trim($data['email']), $assunto, $conteudo, false, 'emails/convite.phtml');            }            $data['usuario'] = $user->id;            $data['data_envio'] = date('Y-m-d H:i:s');            $ModelConvites->insert($data);            //Adiciona a mensagem de sucesso            $this->_helper->FlashMessenger->addMessage(array('sucesso' => 'Enviado com sucesso'));            $this->redirect($_SERVER['HTTP_REFERER']);        }        $this->view->form = $form;        $this->view->dados = $result;    }    public function excluirAction() {        $id = (int) $this->_request->getParam('id');        $ModelConvites = new Admin_Model_UsuarioConvites();        $ModelConvites->delete("id = " . $id);        //Adiciona a mensagem de sucesso        $this->_helper->FlashMessenger->addMessage(array('sucesso' => 'Excluido com sucesso'));        $this->_redirect('admin/convites');    }}