<?phpclass Admin_Model_Categorias extends Zend_Db_Table {    protected $_name = 'produto_categorias';    protected $_primary = 'id';    public function init() {        $viewRenderer = Zend_Controller_Action_HelperBroker::getStaticHelper('ViewRenderer');        $viewRenderer->init();        $this->view = $viewRenderer->view;    }    public function insert(array $data) {        if (!is_array($data))            return false;        if (is_numeric($data['id'])) {            $this->update($data, "id = " . $data['id']);            return $data['id'];        }        unset($data['id']);        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::insert($data_insert);    }    public function Dados() {        $sql = $this->select();        if ($result = $this->fetchRow($sql)) {            return $result;        }    }    public function ListFull() {        $ModelProdutos = new Admin_Model_Produtos();        $sql = $this->select()                ->where("ativo = 1")                ->order("nome");        if ($tipo = $this->fetchAll($sql)) {            $lista = $tipo->toArray();            foreach ($lista as $key => $row) {                $lista[$key]['produtos'] = $ModelProdutos->GetProdutoTipo($row['id']);            }            return $lista;        } else {            return false;        }    }    public function GetUrl($url) {        $sql = $this->select()                ->where('url_tratada = ?', $url);        if ($result = $this->fetchRow($sql)) {            $result = $result->toArray();            $result['subcategorias'] = $this->getChildrensAdmin($result['id']);            return $result;        } else {            return FALSE;        }    }    public function ListarCategorias() {        $sql = $this->select();        if ($categorias = $this->fetchAll($sql)) {            return $categorias->toArray();        } else {            return FALSE;        }    }    public function ListarAll() {        $sql = $this->select();        if ($categorias = $this->fetchAll($sql)) {            return $categorias->toArray();        }    }    public function GetDados($id) {        $sql = $this->select()->where("id = ?", $id);        if ($result = $this->fetchRow($sql)->toArray()) {            return $result;        }    }    public function update(array $data, $where) {        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::update($data_insert, $where);    }    public function Lista() {        $sql = $this->select()->order("id");        $result = $this->fetchAll($sql)->toArray();        return $result;    }    public function ListCategory() {        $sql = $this->select()                ->where("ativo = 1");        if ($categorias = $this->fetchAll($sql)) {            return $categorias->toArray();            ;        } else {            return false;        }    }    public function GetAll() {        $sql = $this->select()->where('ativo = ?', 1)->order("id");        $result = $this->fetchAll($sql)->toArray();        return $result;    }}