<?phpclass Admin_Model_CondicaoPagamento extends Zend_Db_Table {    protected $_name = 'condicao_pagamento';    protected $_primary = 'id';    public function init() {        $viewRenderer = Zend_Controller_Action_HelperBroker::getStaticHelper('ViewRenderer');        $viewRenderer->init();        $this->view = $viewRenderer->view;    }    public function insert(array $data) {        if (!is_array($data))            return false;        if (is_numeric($data['id'])) {            $this->update($data, "id = " . $data['id']);            return $data['id'];        }        unset($data['id']);        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::insert($data_insert);    }    public function Dados() {        $sql = $this->select();        if ($result = $this->fetchRow($sql)) {            return $result;        }    }    public function ListarCondicao() {        $sql = $this->select()                ->where('ativo = ?', 1);        if ($condicao = $this->fetchAll($sql)) {            return $condicao->toArray();        } else {            return FALSE;        }    }    public function Listar() {        $sql = $this->select();        if ($categorias = $this->fetchAll($sql)) {            return $categorias->toArray();        }    }    public function GetDados($id) {        $sql = $this->select()                ->where("id = ?", $id);        if ($result = $this->fetchRow($sql)) {            return $result->toArray();        }    }    public function update(array $data, $where) {        $info = $this->info();        $data_insert = array_intersect_key($data, $info['metadata']);        parent::update($data_insert, $where);    }    public function Lista() {        $sql = $this->select()                ->order("id");        if ($result = $this->fetchAll($sql)) {            return $result->toArray();        }    }}