<?phpclass TCS_Form_FormConvite extends Zend_Form {    public $view = NULL;    public function init() {        $viewRenderer = Zend_Controller_Action_HelperBroker::getStaticHelper('ViewRenderer');        $viewRenderer->init();        $this->view = $viewRenderer->view;        $this->setAction('');        $this->setMethod('post');        $this->setAttribs(array('id' => 'FormConvite', 'name' => 'FormConvite', 'enctype' => 'multipart/form-data'));        $id = $this->createElement('hidden', 'id', array('id' => 'id'));        $id->removeDecorator('HtmlTag')->removeDecorator('Label');        $this->addElement($id);        $elemento = $this->createElement('hidden', 'id', array('id' => 'id'));        $elemento->removeDecorator('HtmlTag')->removeDecorator('Label');        $this->addElement($elemento);        $elemento = $this->createElement('text', 'email', array('id' => 'email', 'Label' => 'E-mail *', 'class' => 'form-control'));        $elemento->setRequired(true)                ->addValidator('emailAddress')                ->addValidator('stringLength');        $this->addElement($elemento);        $elemento = $this->createElement('textarea', 'mensagem', array('label' => 'Mensagem *', 'rows' => '10', 'id' => 'mensagem', 'class' => 'form-control'));        $elemento->setRequired(true);        $elemento->setValue('Convido para a utilização do sistema de Comercialização Eletrônica.');        $this->addElement($elemento);        $elemento = $this->createElement('submit', 'Enviar', array('class' => 'btn btn-success'));        $elemento->removeDecorator('HtmlTag')->removeDecorator('Label');        $this->addElement($elemento);        $this->addDisplayGroup(array('id', 'email', 'mensagem', 'Enviar'), 'form-convite', array('removeDecorator' => 'Label', 'class' => 'form-group'));        $this->setDisplayGroupDecorators(array('FormElements', 'Fieldset'));    }}